// classes.component.ts

import { Component, OnInit } from '@angular/core';
import { Animal, Car } from '../Classes';
import { CarService } from '../class.service';

@Component({
  selector: 'app-car-list',
  templateUrl: './classes.component.html',
  styleUrls: ['./classes.component.css'],
})
export class ClassesComponent implements OnInit {
  cars: Car[] = [];
  constructor(private carService: CarService) {}
  ngOnInit(): void {
    this.cars = this.carService.getCars();
  }
}
@Component({
  selector: 'app-animals',
  templateUrl: './classes.component.html',
  styleUrls: ['./classes.component.css'],
})
export class ClassesComponent2 implements OnInit {
  animals: Animal[] = [];
  constructor(private carService: AnimalService) {}
  ngOnInit(): void {
    this.animals = this.carService.getAnimals();
  }
}
